<script lang="ts">
import Vue from "vue";
import { BASICS, CardColumn, deckBuilderStore as store } from '../../state/DeckBuilderModule';

export default Vue.extend({
  name: 'DeckBuilderSectionControls',

  props: {
    maindeck: {
      type: Boolean
    },
  },

  computed: {
    section(): CardColumn[] {
      return this.maindeck
          ? store.decks[store.selectedSeat].maindeck
          : store.decks[store.selectedSeat].sideboard;
    },
    numCards(): number {
      return this.section.flat().length;
    },
    tooFewCards(): boolean {
      return this.maindeck && this.numCards < 40;
    }
  },

  methods: {
    sortCmc() {
      store.sortByCmc({seat: store.selectedSeat, maindeck: this.maindeck});
    },
    sortColor() {
      store.sortByColor({seat: store.selectedSeat, maindeck: this.maindeck});
    },

    addPlains() {
      this.addLand("Plains", "364", BASICS[0]);
    },

    addIsland() {
      this.addLand("Island", "368", BASICS[1]);
    },

    addSwamp() {
      this.addLand("Swamp", "372", BASICS[2]);
    },

    addMountain() {
      this.addLand("Mountain", "376", BASICS[3]);
    },

    addForest() {
      this.addLand("Forest", "380", BASICS[4]);
    },

    addLand(name: string, collectorNumber: string, mtgo: number) {
      this.section[0].push({
        id: performance.now(),
        sourcePackIndex: 0,
        pickedIn: [],
        hidden: false,
        definition: {
          name,
          set: '10e',
          collector_number: collectorNumber,
          mana_cost: '',
          cmc: 0,
          colors: [],
          color_identity: [],
          mtgo: mtgo,
          rarity: 'common',
          type_line: `Basic Land - ${name}`,
          layout: 'normal',
          card_faces: [],
          foil: false,
          searchName: name.toLocaleLowerCase(),
        }
      });
    },

  },
});
</script>

